mapper.select:
SELECT no,cs_no, cs_title,cs_category_two,cs_regdate,cs_response,cs_respcheck FROM (SELECT ROWNUM no, cs_no, cs_title,cs_category_two,cs_regdate,cs_response,cs_respcheck from (SELECT cs_no,cs_title,cs_category_two,cs_regdate,cs_response,cs_respcheck FROM tb_cs WHERE cs_mem_id = #{cs_mem_id}
AND cs_title like '%' || #{keyword} || '%'
ORDER BY CS_NO DESC)) WHERE no between #{start} and #{end}
